                                                                </div>    
                                                                </div>
                                                                : 
                                                                 <div className="row" key={key}>
                                                                    <div className="col">
                                                                        2
                                                                    </div>
                                                                    <div className="col">
                                                                        ...
                                                                    </div>
                                                                    <div className="col">
                                                                    ...
                                                                    </div>
                                                                    <div className="col">
                                                                    ...
                                                                    </div>
                                                                    <div className="col">                                                                                                        
                                                                    ...
                                                                    </div>
                                                                    <div className="col">
                                                                    ...
                                                                    </div>    
                                                                </div>
                                                            }
                                                            {item.day === 2 ? 
                                                                    <div className="row" key={key}>
                                                                  <div className="col">
                                                                    3
                                                                </div>
                                                                <div className="col">
                                                                    {item.slot === 1 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 2 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 3 && item.name }
                                                                </div>
                                                                <div className="col">                                                                                                        
                                                                 {item.slot === 4 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 5 && item.name }
                                                                </div>    
                                                                </div>
                                                                : 
                                                                 <div className="row" key={key}>
                                                                    <div className="col">
                                                                        3
                                                                    </div>
                                                                    <div className="col">
                                                                        ...
                                                                    </div>
                                                                    <div className="col">
                                                                    ...
                                                                    </div>
                                                                    <div className="col">
                                                                    ...
                                                                    </div>
                                                                    <div className="col">                                                                                                        
                                                                    ...
                                                                    </div>
                                                                    <div className="col">
                                                                    ...
                                                                    </div>    
                                                                </div>
                                                            }
                                                            
                                                            {item.day === 3 ? 
                                                                    <div className="row" key={key}>
                                                                  <div className="col">
                                                                    4
                                                                </div>
                                                                <div className="col">
                                                                    {item.slot === 1 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 2 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 3 && item.name }
                                                                </div>
                                                                <div className="col">                                                                                                        
                                                                 {item.slot === 4 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 5 && item.name }
                                                                </div>    
                                                                </div>
                                                                : 
                                                                 <div className="row" key={key}>
                                                                    <div className="col">
                                                                        4
                                                                    </div>
                                                                    <div className="col">
                                                                        ...
                                                                    </div>
                                                                    <div className="col">
                                                                    ...
                                                                    </div>
                                                                    <div className="col">
                                                                    ...
                                                                    </div>
                                                                    <div className="col">                                                                                                        
                                                                    ...
                                                                    </div>
                                                                    <div className="col">
                                                                    ...
                                                                    </div>    
                                                                </div>
                                                            }
                                                            {item.day === 4 ? 
                                                                    <div className="row" key={key}>
                                                                  <div className="col">
                                                                    5
                                                                </div>
                                                                <div className="col">
                                                                    {item.slot === 1 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 2 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 3 && item.name }
                                                                </div>
                                                                <div className="col">                                                                                                        
                                                                 {item.slot === 4 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 5 && item.name }
                                                                </div>    
                                                                </div>
                                                                : 
                                                                 <div className="row" key={key}>
                                                                    <div className="col">
                                                                        5
                                                                    </div>
                                                                    <div className="col">
                                                                    ...
                                                                    </div>
                                                                    <div className="col">
                                                                    ...
                                                                    </div>
                                                                    <div className="col">
                                                                    ...
                                                                    </div>
                                                                    <div className="col">                                                                                                        
                                                                    ...
                                                                    </div>
                                                                    <div className="col">
                                                                    ...
                                                                    </div>    
                                                                </div>
                                                            }
                                                        </div>
                                                    )}
                                                </div>
                                            </div>
                                            </>
                                            :
                                            <>
                                              <div className="card">
                                               <div className="container text-center">
                                                    <div className="row">
                                                         <div className="col">
                                                        Thứ/Buổi
                                                        </div>
                                                        <div className="col">
                                                        Thứ 2
                                                        </div>
                                                        <div className="col">
                                                        Thứ 3
                                                        </div>
                                                        <div className="col">
                                                         Thứ 4
                                                        </div>
                                                        <div className="col">                                                                                                        
                                                        Thứ 5 
                                                        </div>
                                                        <div className="col">
                                                        Thứ 6
                                                        </div>
                                                    </div>
                                                    {this.state.listSchedule.map((item, key) =>
                                                        <div>
                                                                {item.day === 0 &&
                                                                <div className="row" key={key}>
                                                                  <div className="col">
                                                                    1
                                                                </div>
                                                                <div className="col">
                                                                    {item.slot === 1 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 2 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 3 && item.name }
                                                                </div>
                                                                <div className="col">                                                                                                        
                                                                 {item.slot === 4 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 5 && item.name }
                                                                </div>    
                                                                </div>
                                                                }
                                                              
                                                                  {item.day === 1 &&
                                                                    <div className="row" key={key}>
                                                                  <div className="col">
                                                                    2
                                                                    </div>
                                                                    <div className="col">
                                                                        {item.slot === 1 && item.name }
                                                                    </div>
                                                                        <div className="col">
                                                                    {item.slot === 2 && item.name }
                                                                    </div>
                                                                        <div className="col">
                                                                        {item.slot === 3 && item.name }
                                                                    </div>
                                                                    <div className="col">                                                                                                        
                                                                    {item.slot === 4 && item.name }
                                                                    </div>
                                                                    <div className="col">
                                                                    {item.slot === 5 && item.name }
                                                                    </div>    
                                                                </div>
                                                            }
                                                            {item.day === 2 &&
                                                                <div className="row" key={key}>
                                                                  <div className="col">
                                                                    3
                                                                </div>
                                                                <div className="col">
                                                                    {item.slot === 1 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 2 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 3 && item.name }
                                                                </div>
                                                                <div className="col">                                                                                                        
                                                                 {item.slot === 4 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 5 && item.name }
                                                                </div>    
                                                                </div>
                                                               
                                                            }
                                                            
                                                            {item.day === 3 &&
                                                                    <div className="row" key={key}>
                                                                  <div className="col">
                                                                    4
                                                                </div>
                                                                <div className="col">
                                                                    {item.slot === 1 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 2 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 3 && item.name }
                                                                </div>
                                                                <div className="col">                                                                                                        
                                                                 {item.slot === 4 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 5 && item.name }
                                                                </div>    
                                                                </div>
                                                                
                                                            }
                                                            {item.day === 4 &&
                                                                    <div className="row" key={key}>
                                                                  <div className="col">
                                                                    5
                                                                </div>
                                                                <div className="col">
                                                                    {item.slot === 1 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 2 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 3 && item.name }
                                                                </div>
                                                                <div className="col">                                                                                                        
                                                                 {item.slot === 4 && item.name }
                                                                </div>
                                                                <div className="col">
                                                                 {item.slot === 5 && item.name }
                                                                </div>    
                                                                </div>
                                                            }
                                                        </div>
                                                    )}
                                                    </div>
                                                    
                                                </div>
                                            <div className="card">
                                                        {this.state.student_late.map((item) => {
                                                            return <div>Học sinh {dataUser[0].name} đã đi muộn ngày {new Date(item.date).getDate()}/ {new Date(item.date).getMonth()}</div>
                                                        })}
                                                    </div>
                                            </>
                                }

                                {/* Modal */}
                                <div className="modal fade" id="formemodaledit" style={{ display: 'none' }} aria-hidden="true">
                                    <div className="modal-dialog modal-md modal-dialog-centered">
                                        <div className="modal-content">
                                            <div className="card">
                                                <div className="card-header text-uppercase">Attendance and go home</div>

                                                <div className="card-body">
                                                    <form>
                                                        <div className="row">
                                                            <div className="col-12 col-lg-12 col-xl-12">
                                                                <div className="form-group row">
                                                                    <label className="col-sm-12 col-form-label">Check out</label>
                                                                    <div className="col-sm-10">
                                                                        <label className="col-sm-12 col-form-label">Time Out</label>
                                                                        <div className="col-sm-10">
                                                                            <input name="time_out" className="form-control"
                                                                                onChange={this.handleInputChange} value={new Date().toLocaleTimeString()} readOnly />
                                                                        </div>
                                                                    </div>

                                                                </div>
                                                            </div>
                                                        </div>{/*end row*/}
                                                        <button type="submit" className="btn btn-light px-5" onClick={this.handleEditAttendance}><i className="icon-lock" />Edit</button>
                                                    </form>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default attendance;
